public static Tree meargeSum(Tree firstRoot, Tree secondRoot) {
//	if(firstRoot == null && secondRoot == null) {
//		return null;
//	}
//	int firstValue, secondValue;
	if(firstRoot==null) {
		return secondRoot;
	}
//	else 
//		firstValue = 0;
	
	if(secondRoot==null) {
		return firstRoot;
	}
//	else 
//		secondValue = 0;
	
	firstRoot.data = firstRoot.data+secondRoot.data;
	
	firstRoot.left=meargeSum(firstRoot.left, secondRoot.left);
	firstRoot.right=meargeSum(firstRoot.right, secondRoot.right);
	
	return firstRoot;
}
